INSERT INTO FASE_LUTA(id, nome, descricao) VALUES (1, 'APROXIMAÇÃO', 'Fase de luta que compreende o tempo entre o sinal do árbitro de início de combate (hajime) e a realização do primeiro contato entre os atletas com a tentativa de pegada (kumi-kata).')	;
INSERT INTO FASE_LUTA(id, nome, descricao) VALUES (3, 'ATAQUE/DEFESA', 'Fase de luta que compreende o tempo entre a situação de entrada de golpe entre a preparação (tsukuri) e a projeção (kake) e a situação de defesa e contra-ataque.')	;
INSERT INTO FASE_LUTA(id, nome, descricao) VALUES (4, 'LUTA EM SOLO', 'Fase de luta que compreende o tempo em que os dois lutadores caracterizam a movimentação com o objetivo de executar técnicas de solo (ne-waza), conforme previsto pela regra.')	;
INSERT INTO FASE_LUTA(id, nome, descricao) VALUES (5, 'PAUSA', 'Fase de luta que compreende o tempo entre o sinal de interrupção de luta (mate ou sono-mama) e o sinal de reinício da luta (hajime e yoshi).')	;
INSERT INTO FASE_LUTA(id, nome, descricao) VALUES (2, 'PEGADA', 'Fase de luta que compreende o tempo entre o primeiro contato entre os atletas para tentar realizar a pegada (kumi-kata) e a realização da pegada.')	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (1, 'APROXIMAÇÃO', 'Ações de luta que compreendem os padrões de movimentos realizados na Fase de Aproximação de Luta em Pé.', 1)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (3, 'ATAQUE', 'Ação de luta realizado na Fase de Ataque/Defesa e Projeção na Luta em Pé. É caracterizado pelo ataque, por meio de um golpe de judô, realizado por um lutador com o objetivo de projetar/imobilizar/estrangular/fazer chave de braço o oponente.', 3)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (4, 'DEFESA', 'Ação de luta realizado na Fase de Ataque/Defesa e Projeção na Luta em Pé. É realizado pelo lutador que está sofrendo um ataque com o objetivo de impedir sua projeção.', 3)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (5, 'LUTA EM SOLO', 'Ação de luta realizado na Fase de Luta de Solo. É caracterizado pela ação que realiza a transição da Luta em pé para a Luta de Solo.', 4)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (2, 'PEGADA', 'Ações de luta que compreendem os padrões de movimentos realizados na Fase de Pegada na Luta em Pé. Cada Ação de Pegada é caracterizado pela localização do posicionamento das mãos no judogi do oponente.', 2)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (6, 'COMANDO DE TEMPO', 'Ação de luta realizada pelo Árbitro que influencia no transcorrer do tempo da Luta.', 5)	;
INSERT INTO ACAO_TIPO (id, nome, descricao, id_fase_luta) VALUES (7, 'PENALIZAÇÃO', 'Ação de luta realizada pelo Árbitro na Fase de Pausa que determina uma penalização a um dos lutadores.', 5)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (1, 'ANTEROPOSTERIOR DIREITA', 'Ação de Aproximação de Luta em Pé compreendido pela posição própria de confronto de tentativa de pegada com o pé direito em posição anteroposterior.', 1)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (2, 'ANTEROPOSTERIOR ESQUERDA', 'Ação de Aproximação de Luta em Pé compreendido pela posição própria de confronto de tentativa de pegada com o pé esquerdo em posição anteroposterior.', 1)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (3, 'SEM FORMA', 'Ações de Aproximação de Luta em Pé compreendido por movimentos aleatórios iniciais da luta que não remetem à tentativa de domínio do oponente ou ao posicionamento de preparação para ataque, com os pés em posição anteroposterior.', 1)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (4, 'TENTATIVA DE PEGADA', 'Ação de Aproximação de Luta em Pé compreendido pelo momento em que se inicia o contato entre os lutadores para tentar pegar o judogi do oponente.', 1)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (5, 'DORSAL DIREITA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da região dorsal do judogi do lado direito do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (6, 'DORSAL DIREITA E MANGA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da região dorsal do judogi do lado direito do corpo do oponente e com a outra mão na manga do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (7, 'DORSAL ESQUERDA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da região dorsal do judogi do lado esquerdo do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (8, 'DORSAL ESQUERDA E MANGA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da região dorsal do judogi do lado esquerdo do corpo do oponente e com a outra mão na manga do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (9, 'GOLA DIREITA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da gola do judogi do lado direito do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (10, 'GOLA DIREITA E MANGA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da gola do judogi do lado direito do corpo do oponente e com a outra mão na manga.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (11, 'GOLA ESQUERDA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da gola do judogi do lado esquerdo do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (12, 'GOLA ESQUERDA E GOLA DIREITA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da gola do judogi do lado esquerdo e com a outra na gola do lado direito do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (13, 'GOLA ESQUERDA E MANGA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da gola do judogi do lado esquerdo do corpo do oponente e com a outra mão na manga.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (14, 'MANGA DIREITA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da manga do judogi do lado direito do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (15, 'MANGA ESQUERDA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da manga do judogi do lado esquerdo do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (16, 'MANGA ESQUERDA E MANGA DIREITA', 'Ação de Pegada correspondente ao agarre do lutador com uma das mãos em alguma parte da manga do judogi do lado esquerdo e com a outra na manga do lado direito do corpo do oponente.', 2)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (17, 'ATAQUE', 'Ação de Ataque realizado com alguma Técnica de Projeção (nage-waza), Imobilização (osaekomi-waza), Estrangulamento (shime-waza) ou Chave de Braço (kansetsu-waza).', 3)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (18, 'BLOQUEIO', 'Ação de Defesa em que o lutador que sofre o ataque usa o próprio corpo para bloquear o corpo do lutador que realiza o ataque.', 4)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (19, 'CONTRA-ATAQUE', 'Ação de Defesa em que o lutador que sofre o ataque se antecipa à ação do oponente e executa uma técnica de ataque.', 4)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (20, 'ESQUIVA PARA DIREITA', 'Ação de Defesa em que o lutador que sofre o ataque realiza um movimento de esquiva (tai-sabaki) do seu lado direito para o seu lado esquerdo, sem uso de contra-ataque.', 4)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (21, 'ESQUIVA PARA ESQUERDA', 'Ação de Defesa em que o lutador que sofre o ataque realiza um movimento de esquiva (tai-sabaki) do seu lado esquerdo para o seu lado direito, sem uso de contra-ataque.', 4)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (22, 'LUTA DE SOLO', 'Ação de Luta em que um ou ambos os Lutadores realizam a transição da luta em pé para luta de solo.', 5)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (23, 'HAJIME', 'Comando de tempo realizado pelo Árbitro para sinalizar o início ou reínicio da luta.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (24, 'MATE', 'Comando de tempo realizado pelo Árbitro para sinalizar uma pausa na luta.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (25, 'OSAEKOMI', 'Comando de tempo realizado pelo Árbitro para iniciar a contagem de tempo quando um ataque com técnica de imobilização é bem sucedido.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (26, 'SONOMAMA', 'Comando de tempo realizado pelo Árbitro para paralisar a contagem de tempo, assim como as ações dos lutadores, quando a luta estiver no solo.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (27, 'SOREMADE', 'Comando de tempo realizado pelo Árbitro para sinalizar o fim da luta.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (28, 'TOKETA', 'Comando de tempo realizado pelo Árbitro para finalizar a contagem de tempo de imobilização e indicar que um lutador conseguiu escapar de um ataque bem sucedido.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (29, 'YOSHI', 'Comando de tempo realizado pelo Árbitro para reiniciar a contagem de tempo, assim como as ações dos lutadores, quando a luta no solo tiver sido paralisada após o comando sono-mama.', 6)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (30, 'HANSOKU-MAKE', 'Comando realizado pelo Árbitro para indicar que um dos lutadores recebeu uma penalização grave.', 7)	;
INSERT INTO ACAO (id, nome, descricao, id_tipo) VALUES (31, 'SHIDO', 'Comando realizado pelo Árbitro para indicar que um dos lutadores recebeu uma penalização leve.', 7)	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (1, 'ASHI-WAZA', 'Subdivisão das técnicas de projeção (nage-waza) classificadas como técnica de pé e perna (ashi-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (2, 'KOSHI-WAZA', 'Subdivisão das técnicas de projeção (nage-waza) classificadas como técnica de quadril (koshi-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (3, 'TE-WAZA', 'Subdivisão das técnicas de projeção (nage-waza) classificadas como técnica de mão (te-waza).')	;
