INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (5, 'YOKO-SUTEMI-WAZA', 'Subdivisão das técnicas de projeção (nage-waza) classificadas como técnica de sacrifício lateral (yoko-sutemi-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (6, 'KANSETSU-WAZA', 'Subdivisão das técnicas de aprisionamento (katame-waza) classificadas como técnica de chave de articulação (kansetsu-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (7, 'OSAE-KOMI-WAZA', 'Subdivisão das técnicas de aprisionamento (katame-waza) classificadas como técnica de imobilização (osae-komi-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (8, 'SHIME-WAZA', 'Subdivisão das técnicas de aprisionamento (katame-waza) classificadas como técnica de estrangulamento (shime-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (9, 'UKEMI', 'Subdivisão das técnicas de queda segura (ukemi).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (10, 'ASHI-ATEMI-WAZA', 'Subdivisão das técnicas de ataques contundentes (atemi-waza) classificadas como técnica de perna (ashi-atemi-waza).')	;
INSERT INTO CLASSIFICACAO_KODOKAN (id, nome, descricao) VALUES (11, 'UDE-ATEMI-WAZA', 'Subdivisão das técnicas de ataques contundentes (atemi-waza) classificadas como técnica de braço (ude-atemi-waza).')	;
