INSERT INTO TECNICA_PONTUACAO (id, nome, descricao) VALUES (1, 'IPPON', 'Pontuação máxima – acontece quando um competidor realiza uma técnica de projeção com força e velocidade suficientes de modo que as costas do oponente encostem completamente no solo; na luta de solo quando o oponente é mantido imobilizado por 20 segundos com uso de alguma técnica de imobilização; ou quando desiste ou desmaia ao levar alguma técnica de estrangulamento ou chave de braço.')	;
INSERT INTO TECNICA_PONTUACAO (id, nome, descricao) VALUES (2, 'WAZA-ARI', 'Meio ponto – considerado um “quase ippon”, acontece quando uma projeção não apresenta força ou velocidade suficiente para ser considerada ippon ou quando as costas do oponente não tocam completamente o chão. Também acontece na luta de solo quando o oponente é mantido imobilizado por mais de 15 e menos de 20 segundos.')	;
INSERT INTO TECNICA_PONTUACAO (id, nome, descricao) VALUES (3, 'YUKO', 'Pontuação mínima – também considerada um “quase waza-ari”, acontece quando uma projeção não apresenta dois dos três elementos de um ippon – o oponente encostar completamente as costas no chão, velocidade ou força na execução da técnica. Geralmente é caracterizada quando o oponente é projetado de lado. Acontece na luta de solo quando o oponente é imobilizado por mais de 10 e menos de 15 segundos.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (1, 'FRENTE', 'Direção do golpe em que projeta o lutador na direção para frente.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (2, 'FRENTE DIREITA', 'Direção do golpe em que projeta o lutador na direção diagonal direita para frente.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (3, 'DIREITA', 'Direção do golpe em que projeta o lutador na direção direita.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (4, 'ATRÁS DIREITA', 'Direção do golpe em que projeta o lutador na direção diagonal direita para trás.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (5, 'ATRÁS', 'Direção do golpe em que projeta o lutador na direção atrás.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (6, 'ATRÁS ESQUERDA', 'Direção do golpe em que projeta o lutador na direção diagonal esquerda para trás.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (7, 'ESQUERDA', 'Direção do golpe em que projeta o lutador na direção esquerda.')	;
INSERT INTO TECNICA_DIRECAO (id, nome, descricao) VALUES (8, 'FRENTE ESQUERDA', 'Direção do golpe em que projeta o lutador na direção diagonal esquerda para frente.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (1, 'AÇÃO CONTRA O ESPÍRITO DO JUDÔ', 'Motivo de penalização quando um lutador realiza qualquer ação que seja contra o espírito do judô.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (2, 'ADOTAR POSTURA DESRESPEITOSA', 'Motivo de penalização quando um lutador faz chamadas, comentários ou gestos desrespeitosos ao adversário ou árbitro durante a luta.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (3, 'APLICAR TÉCNICA PROIBIDA', 'Motivo de penalização quando um lutador aplica uma técnica proibida em competição.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (4, 'ARRUMAR OU DESARRUMAR O JUDOGI SEM PERMISSÃO', 'Motivo de penalização quando um lutador arruma ou dessaruma o judogi ou amarra ou desamarra a faixa ou as calças sem a permissão do Árbitro.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (5, 'ATACAR PESCOÇO OU COLUNA DO ADVERSÁRIO', 'Motivo de penalização quando um lutador realiza qualquer ação que possa por em perigo ou machucar o adversário especialmente seu pescoço ou coluna vertebral, ou que possa ser contra o espírito do judô.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (6, 'ATAQUE COM A MÃO OU BRAÇO ABAIXO DA FAIXA', 'Motivo de penalização quando um lutador realiza qualquer ataque ou bloqueio com uma ou as duas mãos ou com um ou os dois braços abaixo da faixa na luta em pé.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (7, 'ATAQUE COM MERGULHO', 'Motivo de penalização quando um lutador &quot;mergulha&quot; de cabeça primeiro sobre o tatami enquanto executa ou tenta executar técnicas tais como uchi-mata, harai-goshi, etc. ou cai diretamente para trás enquanto executa ou tenta uma técnica.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (8, 'ATAQUE FALSO', 'Motivo de penalização quando um lutador realiza uma ação com a intenção de dar a impressão de um ataque mas que claramente demonstra que não existe a intenção de projetar o oponente.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (9, 'ATAQUE TIPO TESOURA', 'Motivo de penalização quando um lutador aplica uma técnica de tesoura com as pernas contra o tronco, pescoço ou cabeça do adversário.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (10, 'CAIR PARA BATER AS COSTAS DO ADVERSÁRIO', 'Motivo de penalização quando um lutador intencionalmente cai de costas quando o adversário está agarrado as suas costas e quando um tem o controle dos movimentos do outro.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (11, 'CHUTAR ADVERSÁRIO', 'Motivo de penalização quando um lutador chuta com o joelho ou o pé a mão ou braço do adversário com o intuito de fazê-lo largar sua pegada ou chutar a perna ou tornozelo do adversário sem aplicar técnica alguma.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (12, 'COLOCAR PÉ OU PERNA NO JUDOGI DO ADVERSÁRIO', 'Motivo de penalização quando um lutador coloca o pé ou perna na faixa, colarinho ou lapela do adversário.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (13, 'DESRESPEITAR COMANDO DO ÁRBITRO', 'Motivo de penalização quando um lutador desrespeita os comando do Árbitro.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (14, 'DOBRAR DEDOS DO ADVERSÁRIO', 'Motivo de penalização quando um lutador dobra para trás os dedos do adversário com intuito de quebrar sua pegada.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (15, 'ESTRANGULAMENTO ILEGAL', 'Motivo de penalização quando um lutador aplica uma técnica de estrangulamento usando a sua faixa ou do oponente, ou a barra da jaqueta, ou usando somente os dedos.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (16, 'EVITAR TENTATIVA DE PEGADA', 'Motivo de penalização quando um lutador intencionalmente evita a tentativa de pegada do oponente para evitar ação na luta.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (17, 'FALTA DE COMBATIVIDADE', 'Motivo de penalização quando um lutador, na luta em pé, antes ou depois da pegada tiver sido estabelecida, não realiza nenhum movimento de ataque.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (18, 'INICIAR NE-WAZA ILEGALMENTE', 'Motivo de penalização quando um lutador puxa o oponente para baixo com o intuito de iniciar a luta de solo sem a realização de um técnica de projeção válida.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (19, 'LEVANTAR E ATIRAR O ADVERSÁRIO NO CHÃO', 'Motivo de penalização quando um lutador levanta um adversário deitado e o atira de volta ao chão.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (20, 'MORDER JUDOGI', 'Motivo de penalização quando um lutador morde o seu próprio judogi ou do oponente.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (21, 'PEGADA "ABRAÇO DE URSO" DIRETAMENTE', 'Motivo de penalização quando um lutador abraça diretamente o oponente para projetá-lo (tipo "abraço de urso").')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (22, 'PEGADA ANORMAL SEM ATACAR', 'Motivo de penalização quando um lutador, na luta em pé, realiza uma pegada considerada anormal sem realizar nenhum ataque.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (23, 'PEGADA PISTOLA NA MANGA', 'Motivo de penalização quando um lutador segura a ponta da manga do oponente entre o dedão e os dedos (pegada "pistola").')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (24, 'PEGADA POR DENTRO DA MANGA OU BARRA DA CALÇA.', 'Motivo de penalização quando um lutador enfia um ou mais dedos por dentro da manga ou barra da calça do oponente.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (25, 'PEGADA TIPO BOLSO NA MANGA', 'Motivo de penalização quando um lutador segura a ponta da manga do oponente dobrando-a para fora (pegada "bolso").')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (26, 'PEGADA DE TORNIQUETE NA MANGA', 'Motivo de penalização quando, na luta em pé, um lutador segura continuamente a ponta da manga do oponente com proposta defensiva ou segura com pegada tipo &quot;torniquete&quot; a manga do oponente.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (27, 'POSTURA DE LUTA MUITO DEFENSIVA', 'Motivo de penalização quando um lutador adota na luta em pé, após o kumi-kata, uma postura excessivamente defensiva.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (28, 'QUEBRAR PEGADA COM AS DUAS MÃOS.', 'Motivo de penalização quando um lutador quebra a pegada do adversário com as duas mãos.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (29, 'SAIR DA ÁREA DE LUTA', 'Motivo de penazalização quando um lutador sai da área de luta ou força o adversário a sair da área de luta.')	;
INSERT INTO MOTIVO_PENALIZACAO (id, nome, descricao) VALUES (30, 'TOCAR O ROSTO DO ADVERSÁRIO', 'Motivo de penalização quando um lutador coloca uma mão, braço, pé ou perna diretamente no rosto do oponente.')	;
